import React from "react";

import Header from './Header'
import Footer from './Footer'

import emailjs from "emailjs-com"
import InputMask from 'react-input-mask';


function Contact() {

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_6mul6cl', 'template_lb05l7u', e.target, 'user_TYASn4RxCENT9Y3o2A4fD')
      .then((result) => {
        alert("Sua mensagem foi enviada com sucesso!")
        window.location.href = "/"
      }, (error) => {
          alert("Erro ao enviar a sua mensagem!")
      });
  }

  class InputPhone extends React.Component {
    state = {
      value: ''
    }

    onChange = (event) => {
      this.setState({
        value: event.target.value
      });
    }

    beforeMaskedValueChange = (newState, oldState, userInput) => {
      var { value } = newState;
      var selection = newState.selection;
      var cursorPosition = selection ? selection.start : null;

      // keep minus if entered by user
      if (value.endsWith('-') && userInput !== '-' && !this.state.value.endsWith('-')) {
        if (cursorPosition === value.length) {
          cursorPosition--;
          selection = { start: cursorPosition, end: cursorPosition };
        }
        value = value.slice(0, -1);
      }

      return {
        value,
        selection
      };
    }

    render() {
      return <InputMask mask="(99)99999-9999" maskChar={null} value={this.state.value} onChange={this.onChange} beforeMaskedValueChange={this.beforeMaskedValueChange} class="CampoFormContato" type="text" name="phone" id="phone" required/>;
    }
  }


  return (

  <div>
  <Header/>
  <body>
  <section class="Contato">    
  <div class="AreaContato">

  <form onSubmit={sendEmail} name="FormContato" class="FormContato">
  <div class="field">
    <label for="name">Nome</label>
    <input class="CampoFormContato" type="text" name="name" id="name" required/>
  </div>

  <div class="field">
    <label for="email">Email</label>
    <input class="CampoFormContato" type="email" name="email" id="email" required/>
  </div>
  <div class="field">
    <label for="phone">Telefone</label>
    <InputPhone/>

  </div>
  <div class="field">
    <label for="message">Mensagem</label>
    <textarea class="CampoFormContato" type="text" name="message" id="message" required/>
  </div>

  <input class="BtEnviar" type="submit" id="button" value="Enviar"/>
</form>


    <div class="InfosContato">
      <div class="LogoContato"><img src="/images/logo_solderox_quem_somos.png" width="362" height="113" alt=""/></div>

      <div class="RedesSociaisContato">
        <a href="https://www.facebook.com/Solderox/" class="BtRedeSocialFacebook"><i></i></a>
        <a href="https://www.linkedin.com/company/solderox/" target="_blank" rel="noopener noreferrer" class="BtRedeSocialLinkedin"><i></i></a>
        <a href="https://www.instagram.com/solderox/" target="_blank" rel="noopener noreferrer" class="BtRedeSocialInstagram"><i></i></a>
      </div>

      <div class="LinhaInfosContato" style={{marginTop: -20}}></div>

      <a href="tel:1131360804"><div class="TelefoneContato"> 11 <b>3136-0804</b></div></a>
      <a class="EmailContato" href="mailto:pablorocha@solderox.com">atendimento@solderox.com.br</a>

      <div class="LinhaInfosContato"></div>    

      <div class="EnderecoContato">SÃ£o Paulo e ABC Paulista / SP</div>
    </div>

    <div class="clear"></div>

  </div>

  <script type="text/javascript"
  src="https://cdn.jsdelivr.net/npm/emailjs-com@2/dist/email.min.js"></script>

<script type="text/javascript">
  emailjs.init('user_TYASn4RxCENT9Y3o2A4fD')
</script>


</section>
</body>

<Footer/>
</div>
  );
}

export default Contact;
