import React from "react";

import { a } from "react-router-dom"

import Header from './Header'
import Footer from './Footer'

import emailjs from "emailjs-com"
import InputMask from 'react-input-mask';
import Swal from 'sweetalert2'


function Contact() {

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_6mul6cl', 'template_lb05l7u', e.target, 'user_TYASn4RxCENT9Y3o2A4fD')
      .then(async (result) => {
        await Swal.fire(
          'Sua mensagem foi enviada com sucesso!',
          'You clicked the button!',
          'success'
        )
        // alert("Sua mensagem foi enviada com sucesso!")
        window.location.href = "/"
      }, async (error) => {
        await Swal.fire({
          icon: 'error',
          title: 'Erro ao enviar a sua mensagem!',
          text: 'Envie um email para atendimento@solderox.com.br'
        })
      });
  }

  class InputPhone extends React.Component {
    state = {
      value: ''
    }

    onChange = (event) => {
      this.setState({
        value: event.target.value
      });
    }

    beforeMaskedValueChange = (newState, oldState, userInput) => {
      var { value } = newState;
      var selection = newState.selection;
      var cursorPosition = selection ? selection.start : null;

      // keep minus if entered by user
      if (value.endsWith('-') && userInput !== '-' && !this.state.value.endsWith('-')) {
        if (cursorPosition === value.length) {
          cursorPosition--;
          selection = { start: cursorPosition, end: cursorPosition };
        }
        value = value.slice(0, -1);
      }

      return {
        value,
        selection
      };
    }

    render() {
      return <InputMask mask="(99)99999-9999" maskChar={null} value={this.state.value} onChange={this.onChange} beforeMaskedValueChange={this.beforeMaskedValueChange} className="CampoFormContato" type="text" name="phone" id="phone" required/>;
    }
  }


  return (

  <>
  <Header/>

  <section className="Contato">    
  <div className="AreaContato">

  <form onSubmit={sendEmail} name="FormContato" className="FormContato">
  <div className="field">
    <label htmlFor="name">Nome</label>
    <input className="CampoFormContato" type="text" name="name" id="name" required/>
  </div>

  <div className="field">
    <label htmlFor="email">Email</label>
    <input className="CampoFormContato" type="email" name="email" id="email" required/>
  </div>
  <div className="field">
    <label htmlFor="phone">Telefone</label>
    <InputPhone/>

  </div>
  <div className="field">
    <label htmlFor="message">Mensagem</label>
    <textarea className="CampoFormContato" type="text" name="message" id="message" required/>
  </div>

  <input className="BtEnviar" type="submit" id="button" value="Enviar"/>
</form>


    <div className="InfosContato">
      <div className="LogoContato"><img src="/images/logo_solderox_quem_somos.png" width="362" height="113" alt=""/></div>

      <div className="RedesSociaisContato">
        <a href="https://www.facebook.com/Solderox/" className="BtRedeSocialFacebook"><i></i></a>
        <a href="https://www.aedin.com/company/solderox/" target="_blank" rel="noopener noreferrer" className="BtRedeSocialaedin"><i></i></a>
        <a href="https://www.instagram.com/solderox/" target="_blank" rel="noopener noreferrer" className="BtRedeSocialInstagram"><i></i></a>
      </div>

      <div className="LinhaInfosContato" style={{marginTop: -20}}></div>

      <a href="tel:1131360804"><div className="TelefoneContato"> 11 <b>3136-0804</b></div></a>
      <a className="EmailContato" href="mailto:pablorocha@solderox.com">atendimento@solderox.com.br</a>

      <div className="LinhaInfosContato"></div>    

      <div className="EnderecoContato">São Paulo e ABC Paulista / SP</div>
    </div>

    <div className="clear"></div>

  </div>

  <script type="text/javascript"
  src="https://cdn.jsdelivr.net/npm/emailjs-com@2/dist/email.min.js"></script>

<script type="text/javascript">
  emailjs.init('user_TYASn4RxCENT9Y3o2A4fD')
</script>


</section>


<Footer/>
</>
  );
}

export default Contact;
