import React from "react";

import {
  CarouselProvider,
  Slider,
  Slide,
  ButtonBack,
  ButtonNext,
  Image,
  DotGroup,
} from "pure-react-carousel";
import "pure-react-carousel/dist/react-carousel.es.css";

export default class extends React.Component {
  render() {
    return (
      <CarouselProvider
        naturalSlideWidth={100}
        // naturalSlideHeight={200}
        isIntrinsicHeight={true}
        totalSlides={2}
        interval={5000}
        infinite={true}
        isPlaying={true}
        hasMasterSpinner={true}
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Slider>
          <Slide index={0}>
            <Image src="images/banner_01.jpg" alt="banner1" />
          </Slide>
          <Slide index={1}>
            <Image src="images/banner_02.jpg" alt="banner2" />
          </Slide>
          {/* <Slide index={2}><Image src="images/foto_noticia.jpg" alt="banner2"/></Slide> */}
        </Slider>
        <ButtonBack
          className={"bottomBanner"}
          style={{
            zIndex: 2,
            position: "absolute",
            backgroundColor: "transparent",
            border: "none",
            left: 0,
            height:"18%",
            outline: "none"
          }}>
          <Image
            src="images/arrowLeft.png"
            style={{
              opacity: 0.2,
            }}
          />
        </ButtonBack>
        <DotGroup
          className={"dot"}
          style={{
            zIndex: 1,
            position: "absolute",
            marginTop: "17%",
          }}
        />

        <ButtonNext
          className={"bottomBanner"}
          style={{
            zIndex: 2,
            position: "absolute",
            backgroundColor: "transparent",
            border: "none",
            right: 0,
            height:"18%",
            outline: "none"
          }}
        >
          <Image
            src="images/arrowRight.png"
            style={{
              opacity: 0.2,
            }}
          />
        </ButtonNext>
        {/* <Image
            src="images/arrowRight.png"
            style={{
              opacity: 0.2,
            }}
          /> */}
      </CarouselProvider>
    );
  }
}
